label("$C$", C, NE);
label("$D$", D, NE);
label("$E$", E, NW);
label("$F$", F, NW);
label("$X$", X, SW);
label("$Y$", Y, SE);
label("$Z$", Z, N);
label("$1$", (A + B)/2, S);
